sys_script_client.script:
function onLoad() {
    function showModal() {
        setTimeout(function() {
            g_modal.showFrame({
                url: '/x_paha_admin_suppo_Special%20Handling.do?sysparm_id=' + g_form.getUniqueValue(),
                title: 'Special Handling',
                size: 'lg',
                height: 300
            });

        }, 1000);
    }

    function gotResponse(response) {
        console.log(response);

        if (response.recordSet && response.recordSet.length > 0) {
            showModal();
        }
    }

    var details = new GlideRecord('x_paha_admin_suppo_special_handling_detail');
    details.addQuery('parent.sys_id', g_form.getUniqueValue());
    details.query(gotResponse);
}
