sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if ((isLoading && !g_form.isNewRecord()) || (g_form.isLiveUpdating && g_form.isLiveUpdating()))
      return;

   if (newValue == '' || newValue == null) {
      g_form.setValue('location', '');
      return;
   }
   if (!g_form.hasField('location'))
      return;
   var jobRequestor = g_form.getReference('u_job_requestor', setLocation);
}

function setLocation(jobRequestor) {
   if (jobRequestor)
       g_form.setValue('location', jobRequestor.location);
}
