sys_ui_action.script:
var timeWorkedGr = new GlideRecord('task_time_worked');
timeWorkedGr.addQuery('task',current.sys_id);
timeWorkedGr.query();
//gs.info("RDS: current.sys_id: " + current.sys_id);
//gs.info("RDS: rows: " + timeWorkedGr.getRowCount());

if (timeWorkedGr.getRowCount() == "0") {
	gs.addErrorMessage("Please create a Time Worked record before closing");
} else {
	//g_form.setValue('state', 3);
	//g_form.save();
	current.state = 3;
	current.update();
}

action.setRedirectURL(current);
action.setReturnURL(current);

