sys_ui_action.client_script_v2:
function onClick(g_form) {
//function reopen(){
  var con = prompt("Please enter your reason for declining the work.");
  if(con != ''){
   g_form.setValue('comments', con);
   g_form.setValue('declined_work', true);
   g_form.setValue('assigned_to', '');
   g_form.setValue('state', 1);
   g_form.save();

   gsftSubmit(gel('sysverb_update_and_stay'));
    //gsftSubmit(gel('sysverb_update'));
  }
  else{
    return false;
  }
//}
}

sys_ui_action.script:
function reopen(){
  var con = prompt("Please enter your reason for declining the work.");
  if(con != ''){
   g_form.setValue('comments', con);
   g_form.setValue('declined_work', true);
   g_form.setValue('assigned_to', '');
   g_form.setValue('state', 1);
   g_form.save();

   gsftSubmit(gel('sysverb_update_and_stay'));
    //gsftSubmit(gel('sysverb_update'));
   g_form.save();

  }
  else{
    return false;
  }
}

