sys_ui_action.client_script_v2:
function onClick(g_form) {
	var close_notes = g_form.getValue('close_notes');

   if (close_notes == '') {
        g_form.setMandatory('close_notes', true);
        g_form.save();
   }
   else{
      var timeWorkedGr = new GlideRecord('task_time_worked');
      timeWorkedGr.addQuery('task',current.sys_id);
      timeWorkedGr.query();

      if (timeWorkedGr.getRowCount() == "0") {
        gs.addErrorMessage("Please create a Time Worked record before closing");
      } else {
        g_form.setValue('state', 4);
        g_form.save();
         //return false;
     }
}

}

sys_ui_action.script:
function close_incomplete(){
   var close_notes = g_form.getValue('close_notes');

   if (close_notes == '') {
        g_form.setMandatory('close_notes', true);
        g_form.save();
   }
   else {
      var timeWorkedGr = new GlideRecord('task_time_worked');
      timeWorkedGr.addQuery('task',current.sys_id);
      timeWorkedGr.query();

      if (timeWorkedGr.getRowCount() == "0") {
        gs.addErrorMessage("Please create a Time Worked record before closing");
      } else {
gs.addErrorMessage("Yo!");
        g_form.setValue('state', 4);
        g_form.save();
         //return false;
     }
}

}
